import { Link, NavLink, useLocation } from 'react-router-dom';
import { useLogout } from '../hooks/useLogout';
import { useAuthContext } from '../hooks/useAuthContext';
import logo from './8b9667eb83349fb1db1711b86efeeef5-removebg-preview.png';

const Navbar = () => {
  const { logout } = useLogout();
  const { user } = useAuthContext();
  const location = useLocation();

  const handleClick = () => {
    logout();
  };

  return (
    <header className="navbar">
      <nav className="container">
        <Link to="/" className="site-title">
          <img src={logo} alt="Logo" />
        </Link>
        <ul className="nav-links">
          <li>
            <NavLink
              to="/blogs"
            >
              Blogs
            </NavLink>
          </li>
          {user && <li>
            <NavLink to="/createblog">
              New Blog
            </NavLink>
          </li>}
          {user && <li>
          <NavLink to="/myBlogs" >
              My Blogs
            </NavLink>
          </li>}
          <li>
            <NavLink to="/about">
              About
            </NavLink>
          </li>
          <li>
            {user ? (
              <div>
                <li><span>{user.username}</span></li>
                

                <li><button onClick={handleClick}>Log out</button></li>
              </div>
            ) : (
              <div>
              <NavLink to="/login" className="btnLogin">
                Login
              </NavLink>
<br/>
              <NavLink to="/signup" className="btnSignup">
                Signup
              </NavLink>
              </div>

            )}
          </li>
        </ul>
      </nav>
    </header>
  );
};

export default Navbar;
