import { Link, NavLink, useLocation } from 'react-router-dom';
import { useLogout } from '../hooks/useLogout';
import { useAuthContext } from '../hooks/useAuthContext';
import logo from './8b9667eb83349fb1db1711b86efeeef5-removebg-preview.png';

const Navbar = () => {
  const { logout } = useLogout();
  const { user } = useAuthContext();
  const location = useLocation();

  const handleClick = () => {
    logout();
  };

  return (
    <header className="navbar">
      <nav className="container">
        <Link to="/" className="site-title">
          <img src={logo} alt="Logo" />
        </Link>
        <ul className="nav-links">
          <li>
            <NavLink
              to="/blogs"
              activeClassName="active"
              isActive={(match, location) =>
                location.pathname === '/' || location.pathname.startsWith('/blogs')
              }
            >
              Blogs
            </NavLink>
          </li>
          {user && <li>
            <NavLink to="/createblog" activeClassName="active">
              New Blog
            </NavLink>
          </li>}
          {user && <li>
          <NavLink to="/myBlogs" activeClassName="active">
              My Blogs
            </NavLink>
          </li>}
          <li>
            <NavLink to="/about" activeClassName="active">
              About
            </NavLink>
          </li>
          <li>
            {user ? (
              <div>
                <span>{user.username}</span>
                <button onClick={handleClick}>Log out</button>
              </div>
            ) : (
              <div>
              <NavLink to="/login" activeClassName="active" className="btnLogin">
                Login
              </NavLink>

              <NavLink to="/signup" activeClassName="active" className="btnSignup">
                Signup
              </NavLink>
              </div>

            )}
          </li>
        </ul>
      </nav>
    </header>
  );
};

export default Navbar;
