import React, { useState } from "react";
import { useHistory } from "react-router-dom";

const Createblog = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState(""); // Define the 'content' variable
  const [author, setAuthor] = useState("");
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();

    const newBlog = {
      title: title,
      content: content, // Use the 'content' variable
      author: author,
    };

    fetch("http://localhost:8000/blogs", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newBlog),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to create a new blog");
        }
        return response.json();
      })
      .then((data) => {
        // Handle successful response
        console.log(data);
        history.push("/");
      })
      .catch((error) => {
        // Handle error
        console.log(error);
      });
  };

  return (
    <div className="create-blog">
      <h2>Create a New Blog</h2>
      <form onSubmit={handleSubmit}>
        <label>Title:</label>
        <input
          type="text"
          required
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <label>Content:</label>
        <textarea
          required
          value={content}
          onChange={(e) => setContent(e.target.value)} // Update the 'content' value
        ></textarea>
        <label>Author:</label>
        <input
          type="text"
          required
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        />
        <button>Create Blog</button>
      </form>
    </div>
  );
};

export default Createblog;
