import "./styles.css";
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom'
import { useAuthContext } from './hooks/useAuthContext'

import Navbar from "./components/Navbar"
import About from "./pages/About"
import Blogs from "./pages/Blogs"
import Home from "./pages/Home"
import Login from "./pages/Login"
import Createblog from "./pages/Createblog"
import BlogInfo from "./pages/BlogInfo"
import Signup from "./pages/Signup";


//!(Important note)!:  to start and make the blog list work use: npx json-server --watch data/db.json --port 8000 (outside frontend)
// and then use npm start

//cannot run this pc's terminal so:
// open terminal and npm install react-router-dom in src
// use "npx json-server --watch data/db.json --port 8000" which will launch the loading of the blogs
// npm start anywhere
// result should give a working viewing blog and making blog. report if any unexpected issues occur
function App() {
  const { user } = useAuthContext()

  return (
    <div className="App">
      <BrowserRouter>
        <Navbar />
        <div className="content-container">
          <Routes>
            <Route 
              path="/" 
              element={user ? <Home /> : <Navigate to="/login" />} 
            />
            <Route 
              path="/login" 
              element={!user ? <Login /> : <Navigate to="/" />} 
            />
            <Route 
              path="/signup" 
              element={!user ? <Signup /> : <Navigate to="/login" />} 
            />
            <Route path="/blogs" element={<Blogs />} />
            <Route path="/about" element={<About />} />
            <Route path="/createblog" element={<Createblog />} />
            <Route path="/blogs/:id" element={<BlogInfo />} /> 
          </Routes>
        </div>
      </BrowserRouter>
    </div>
  );
  //!(Do not add a button to bloginfo as it is what is used to view your own blog that you have written and shouldn't be accessed from the navigation bar)!.
}

export default App;
